function del_topic_fav (arr){
	var topic_id = getUrlVars()['showtopic'];
	if ((topic_id!==null) && (arr !== false)){
		var index = arr[0].indexOf(parseInt(topic_id));
		if (index != -1){
			arr[0].splice(index,1);
			delete arr[1][topic_id];
			setFavStorage (arr);
		}
	}
	return arr;
}

function getFavStorage (){
	var str = window.localStorage.getItem('4pda_script_fav');
	var arr = [];
	if (str !== '' && str !==null){
		var tmp = [];
		arr[0] = [];
		arr[1] =[];
		tmp = str.toString().split('\r\n\r\n');
		for (var i=0;i<tmp.length;i++){
			arr[0][i]=parseInt(tmp[i].substr(0,tmp[i].indexOf('\r\n')));
			arr[1][arr[0][i]] = tmp[i].substr(tmp[i].indexOf('\r\n')).replace(/\r\n/g,'');
		}
	}
	return arr;
}

function setFavStorage (arr){
	if (arr !== false){
		var result ='';
		for (var i =0; i<arr[0].length;i++){
			result = result + arr[0][i].toString()+'\r\n'+arr[1][arr[0][i]]+'\r\n\r\n';
		}
		window.localStorage.setItem('4pda_script_fav',result.substr(0,result.length-4));
	}else{
		window.localStorage.setItem('4pda_script_fav','');
	}
}

