//Получение параметра из URL
function getUrlVars() {
	var vars = {};
	var parts = window.location.href.replace(/[?&]+([^=&]+)=([^&]*)/gi, function(m,key,value) {
		vars[key] = value;
	});
	if (window.location.href==parts)
		return false;
	else
		return vars;
}

